// Dark Mode Toggle
function toggleDarkMode() {
    document.body.classList.toggle('dark-mode');
    localStorage.setItem('darkMode', document.body.classList.contains('dark-mode'));  // Save user preference
}

// Apply saved dark mode on page load
if (localStorage.getItem('darkMode') === 'true') {
    document.body.classList.add('dark-mode');
}

// Smooth Scroll for Internal Links
document.querySelectorAll('a[href^="#"]').forEach(anchor => {
    anchor.addEventListener('click', function(e) {
        e.preventDefault();
        document.querySelector(this.getAttribute('href')).scrollIntoView({
            behavior: 'smooth'
        });
    });
});

// Collapsible Job Sections with Animation
document.querySelectorAll('.job h3').forEach((jobTitle) => {
    jobTitle.addEventListener('click', function() {
        const jobDetails = this.nextElementSibling;
        if (jobDetails.style.display === 'block') {
            jobDetails.style.display = 'none';
        } else {
            jobDetails.style.display = 'block';
            jobDetails.style.maxHeight = jobDetails.scrollHeight + 'px';  // Smooth expansion
        }
    });
});

// Animated Section Reveal on Scroll
const revealElements = document.querySelectorAll('.section');
const revealOnScroll = () => {
    const windowHeight = window.innerHeight;
    revealElements.forEach(section => {
        const revealTop = section.getBoundingClientRect().top;
        if (revealTop < windowHeight - 100) {  // 100px threshold
            section.classList.add('active');
        } else {
            section.classList.remove('active');
        }
    });
};
window.addEventListener('scroll', revealOnScroll);

// Back to Top Button
const backToTopButton = document.createElement('div');
backToTopButton.innerHTML = 'â¬† Back to Top';
backToTopButton.className = 'back-to-top';
document.body.appendChild(backToTopButton);

window.addEventListener('scroll', function() {
    if (window.scrollY > 300) {
        backToTopButton.style.display = 'block';
    } else {
        backToTopButton.style.display = 'none';
    }
});

backToTopButton.addEventListener('click', function() {
    window.scrollTo({ top: 0, behavior: 'smooth' });
});
