// JavaScript for resume website

// 1. Expand/Collapse Job Descriptions
document.querySelectorAll('.job-header').forEach(header => {
    header.addEventListener('click', () => {
        const content = header.nextElementSibling;
        content.classList.toggle('expanded');
        if (content.classList.contains('expanded')) {
            content.style.maxHeight = content.scrollHeight + 'px';
        } else {
            content.style.maxHeight = '0px';
        }
    });
});

// 2. Loading Screen Animation
window.addEventListener('load', () => {
    const loader = document.getElementById('loader');
    loader.style.display = 'none'; // Hide the loader once the page is fully loaded
});

// 3. Smooth Scroll for Navigation Links
document.querySelectorAll('a[href^="#"]').forEach(anchor => {
    anchor.addEventListener('click', function (e) {
        e.preventDefault();
        document.querySelector(this.getAttribute('href')).scrollIntoView({
            behavior: 'smooth'
        });
    });
});

// 4. Contact Form Validation (if applicable)
document.getElementById('contactForm').addEventListener('submit', function (e) {
    e.preventDefault();
    const emailField = document.getElementById('email');
    const messageField = document.getElementById('message');
    
    if (!validateEmail(emailField.value)) {
        alert('Please enter a valid email address.');
        return;
    }

    if (messageField.value.trim() === '') {
        alert('Please enter your message.');
        return;
    }

    // If everything is good, submit the form
    this.submit();
});

function validateEmail(email) {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(String(email).toLowerCase());
}
