// JavaScript for Expand/Collapse Functionality
document.addEventListener("DOMContentLoaded", function() {
    // Select all job titles
    const jobTitles = document.querySelectorAll('.job-title');

    // Add event listeners to each job title for expand/collapse functionality
    jobTitles.forEach((job) => {
        job.addEventListener('click', function() {
            const description = this.nextElementSibling;

            // Toggle description visibility with smooth animation
            if (description.style.maxHeight) {
                description.style.maxHeight = null;  // Collapse the description
            } else {
                description.style.maxHeight = description.scrollHeight + "px";  // Expand the description
            }
        });
    });
});
